mapper.select:
SELECT v.video_id, v.title, v.description, v.create_time, v.image_path, v.release_time, vd.views, vd.likes, v.tags, v.type, u.nick_name as nickName, u.user_id as userId, c.category_id, c.name as categoryName FROM vt_video v LEFT JOIN vt_video_data vd ON v.video_id = vd.video_id LEFT JOIN vt_user u ON vd.user_id = u.user_id LEFT JOIN vt_video_category vc ON v.video_id = vc.video_id LEFT JOIN vt_category c ON vc.category_id = c.category_id WHERE 1=1
AND (
FIND_IN_SET(#{tag}, tags) > 0
)
AND v.title LIKE concat('%', #{video.title}, '%')
AND u.user_id = #{video.userId}
AND v.type = #{video.type}
AND c.category_id = #{video.categoryId}
AND DATE(v.create_time) = #{video.createTime}
ORDER BY v.create_time DESC

SELECT v.video_id, v.title, v.description, v.create_time, v.image_path, vd.views, vd.likes, v.tags, v.type, u.nick_name as nickName, u.user_id as userId, c.category_id, c.name as categoryName FROM vt_video v LEFT JOIN vt_video_data vd ON v.video_id = vd.video_id LEFT JOIN vt_user u ON vd.user_id = u.user_id LEFT JOIN vt_video_category vc ON v.video_id = vc.video_id LEFT JOIN vt_category c ON vc.category_id = c.category_id LEFT JOIN vt_video_update vu ON v.video_id = vu.video_id WHERE 1=1
AND (
FIND_IN_SET(#{tag}, tags) > 0
)
AND v.title LIKE concat('%', #{video.title}, '%')
AND u.user_id = #{video.userId}
AND v.type = #{video.type}
AND c.category_id = #{video.categoryId}
AND DATE(v.create_time) = #{video.createTime}
and vu.video_id IS NULL ORDER BY v.create_time DESC

SELECT v.video_id, v.title, v.release_time, v.image_path, vd.views, vd.likes, v.tags, v.type, c.NAME AS categoryName, LEAST((COALESCE(vd.likes, 0) / 1000) * 5, 5) AS make FROM vt_video AS v LEFT JOIN vt_video_data AS vd ON v.video_id = vd.video_id LEFT JOIN vt_video_category AS vc ON v.video_id = vc.video_id LEFT JOIN vt_category AS c ON vc.category_id = c.category_id WHERE 1=1
AND (
FIND_IN_SET(#{tag}, tags) > 0
)
AND v.title LIKE concat('%', #{video.title}, '%')
AND v.type = #{video.type}
AND c.category_id = #{video.categoryId}
AND YEAR(v.release_time) = YEAR(#{video.releaseTime})

SELECT v.video_id AS videoId, v.title, v.image_path AS imagePath, v.create_time AS createTime, vd.views, vd.likes, v.review_status FROM vt_video v INNER JOIN vt_video_data vd ON v.video_id = vd.video_id WHERE vd.user_id = #{userId}
AND v.title LIKE CONCAT('%', #{title}, '%')
order by v.create_time DESC

mapper.update:

UPDATE vt_video_data
SET user_id = #{userId},
views = #{views},
likes = #{likes}
WHERE video_id = #{videoId};


UPDATE vt_video_category
SET category_id = #{categoryId}
WHERE video_id = #{videoId};
